import {React  , useState } from 'react'
import shortid from  'shortid';
import './TodoForm.css'
const TodoForm = (props) => {
    let [ text , setText ] = useState("");
    let handlevent = (e) => {
        e.preventDeFault()
        props.onSubmit({
            id: shortid.generate() , 
            text : text , 
            complete: false
        }) 
    }

    return ( 
    <form onSubmit={handlevent}>
        <input
         type="text"
         className=' shadow p-3 mb-5 bg-white rounded' 
         id= "input" 
         onChange={(e) => setText( e.target.value)}  
         value={text}
         />
        <button className='  btn shadow p-3 mb-5 bg-white rounded'  onClick={handlevent} >Add Todo</button>

    </form>
    ); 
}

export default TodoForm
