import {  React , useState } from 'react'
import {TodoForm , Todo } from './componets/index'
import './App.css'
const App = () => {
  let [ todos , setTodos ] = useState([ ]);
  let [ todoToShow , setTodoToShow ] = useState('all')
  const addTodo = (todo) => {  
    setTodos([todo , ...todos])
  };
  let handlDelete = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id))
  }
 let updateTodoShow  = (s) => {
  setTodoToShow(s);
} 
 let toggleComplete = (id) => {
   setTodos(
    todos.map((todo) => {
    if( todo.id === id ) {
      return {
        ...todo , 
        complete : !todo.complete
      }
    } else {
      return todo;
    }}))}
 if (todoToShow === 'active') {
  todos = todos.filter((todo) => !todo.complete)
 } else if (todoToShow === 'complete') {
  todos = todos.filter((todo) => todo.complete)
 }
  return ( 
    <div className='Container' >
      <TodoForm onSubmit={addTodo}/>
      {
        todos.map((todo) =>(
          <Todo 
          key ={todo.id}
          todo={todo}
          onDelete = {() => handlDelete(todo.id)}
          toggleComplete={() => toggleComplete(todo.id)}
          />
        ))}
        <div className='up-btn'>
          <button className='update-btn shadow bg-white '
          onClick={( ) => updateTodoShow('all')}
          >All</button>
          <button className='update-btn shadow bg-white '
          onClick={( ) => updateTodoShow('active')}
          >Active</button>
          <button className='update-btn shadow bg-white '
          onClick={( ) => updateTodoShow('complete')}
          >Complete</button>
        </div>
        <button>Remove all complete todos</button>
    </div>
  )
}

export default App
