import './App.css'
import {React , useState } from 'react'
const App = () => {
  let input = document.getElementById('input'); 
  let [ todolist , setTodolist ] = useState( [
    { input: input.value}
  ])
  let clickEvent = () => {

   setTodolist( todolist  )
  }
  return (
    <div className="todolist ">

      <div className='todolist-input '>
      <input id= "input" type="text" className='bg-dark shadow p-3 mb-5 bg-white rounded' ></input>
      <button className='  btn shadow p-3 mb-5 bg-white rounded' onClick={clickEvent} >Add Todo</button>
      { todolist 
      }
      </div>
      <div id="tasks"></div>
      <div className='todolist-Features '>
        <button  className=' btn shadow p-3 mb-5 bg-white rounded'>All</button>
        <button  className=' btn shadow p-3 mb-5 bg-white rounded'>Active</button>
        <button  className=' btn shadow p-3 mb-5 bg-white rounded'>Complete</button>
      </div>

    </div>
  );
}

export default App
